import './styles.css';
import PieChartCard from '../pie-chart-card';
import { useEffect, useState } from 'react';
import { makeRequest } from '../../utils/request';
import { SalesByGender, ChartSeriesData, PieChartConfig } from '../../../types';
import { buildChartSeries } from './helpers';

function SalesByStore() {
  const [chartSeries, setChartSeries] = useState<ChartSeriesData[]>([]);
  const [salesByGenderData, setSalesByGenderData] = useState<PieChartConfig>();

  useEffect(() => {
    makeRequest.get<SalesByGender[]>('sales/by-gender?storeId=0').then((response) => {
      const newChartSeries = buildChartSeries(response.data);
      setChartSeries(newChartSeries);
    });
  }, []);
  return (
    <div className="sales-by-store-container base-card">
      <div className="sales-by-store-data">
        <h1>Total</h1>
        <span>Total de Vendas</span>
      </div>
      <div className="pie-chart-card">
        <PieChartCard name="" labels={salesByGenderData?.labels} series={salesByGenderData?.series} />
      </div>
    </div>
  );
}

export default SalesByStore;
